import {component} from 'react';
class changePageColor extends component {
  constructor(props)
  {
    super(props);
    this.state ={color:"blue"};
  }
  render(){
    return(<div id="mnt"><b> In Mounting Phase</b></div>);

  }
  componentDidMount(){
    let dv=document.getElementById("mnt");
    dv.innerHTML += "<b><u> Component Mounted</u></b>";
    dv.style.color=state.color;
  }
  componentDidUpdate(){
    let dv=document.getElementById("mnt");
    dv.innerHTML += "<br><b><i>Component updated</b></i>";
  }
}
export default changePageColor;
