import {component} from 'react';
class changePageColor extends component {
  constructor(props)
  {
    super(props);
    this.state ={color:"blue"};
  }
  render(){
    return(<div id="mnt"><b> In Mounting Phase</b>
    <select defaultValue={this.state.color}>
      <option value="red"> Red</option>
      <option value="orange">Orange</option>
      <option value="Yellow">Yellow</option>
      <option value="Brown">Brown</option>
      <option value="indigo">Indigo</option>
    </select>
    </div>);

  }
  componentDidMount(){
    let dv=document.getElementById("mnt");
    dv.innerHTML += "<b><u> Component Mounted</u></b>";
    dv.style.color=this.state.color;
  }
  componentDidUpdate(){
    let dv=document.getElementById("mnt");
    dv.innerHTML += "<br><b><i>Component updated</b></i>";
  }
}
export default changePageColor;
